import React, {useState} from 'react';
import ModalHeader from './ModalHeader'

const useListFormBuilder = () => {
    
    var build = ({title, list, onSubmit, onCancel}) => {
        return {
            header: <ModalHeader title={title} onCancel={onCancel}/>,
            body: 
                    <ListDetailsFormBody location={list}></ListDetailsFormBody>,
            footer: 
                    <ListDetailsFooter onSubmit={onSubmit}></ListDetailsFooter>
        }
    }

    return build;
}

const ListDetailsFooter = ({onSubmit}) => {
    return(
        <div className="location-edit-form-buttons">
            {<button 
                    type="button" 
                    className="btn btn-primary" 
                    onClick={() => 
                        onSubmit({})}>
                Save</button>}
        </div>
    )
}


const ListDetailsFormBody = (props) => {
    const [state, setState] = useState(props.list);
    console.log(props);
    console.log(state);

    var handleInputChanged = (e) => {
        console.log(e.target);
        setState(e)
    }

    return(
        <div className="list-form-container">
            <div className="list-form-item">
                <label htmlFor="list-name" className="col-form-label">Name (required)</label>
                <input 
                    name="name" 
                    className="form-control" 
                    id="list-name" 
                    onClick={handleInputChanged}
                    value={state.name || ''}
                    autoFocus/>
            </div>
            <div className="list-form-item">
                <label htmlFor="list-description">Description</label>
                <textarea 
                    name="description" 
                    className="form-control" 
                    rows="5" id="list-description" 
                    value={state.description || ''}></textarea>
            </div>
            <div className="list-form-item">
                
            <label htmlFor="list-private">Is Private? </label>
                <input type="checkbox" id="list-private" value="isPrivate"/> 
            </div>
        </div>
    )
}

export default useListFormBuilder;
