import React, { useState } from 'react'

const DropDown = ({options, onSelect}) => {
    const [optionsVisible, setOptionsVisible] = useState(false)
    //const options = [{name: 'option1', key: 1}, {name: 'option', key: 2}]

    var handleOptionSelected = (id) => {
        console.log(id);
        onSelect(id);
    }

    var toggleOptions = () => {
        setOptionsVisible(!optionsVisible);
    }

    return (
        <div className="drop-down-container">
            <div className="drop-down-title-container" onClick={()=>toggleOptions()}>
                <div className="drop-down-title-name">title</div>
                <div className="drop-down-title-arrow">^</div>
            </div>
            {optionsVisible && <ul className="drop-down-options-list">
                {options.map((option)=>(
                    <li className="drop-down-option" key={option.key} onClick={()=>handleOptionSelected(option.key)}>{option.name}</li>
                ))}
            </ul>}
        </div>)
}

export default DropDown
