const styles = {
    error: 'background: red; color white;font-size: 12pt',
    warning: "background: yellow; color: black; font-size: 11pt;display: block",
    info: 'background: white; color: black',
    debug: 'background: coral; color: black'
}

const useConsoleLog = () => {
    var _log = (message, style) => {
        console.log(`%c${message}`, style);
    }

    var error = (logEntry) => {
        console.groupCollapsed(`%c${logEntry}`, styles.error);
        if(logEntry.data){
            console.log(logEntry.data)
        }
        console.trace();
        console.groupEnd();
    }

    var warning = (logEntry) => {
        console.groupCollapsed(`%c${logEntry}`, styles.warning);
        console.trace();
        console.groupEnd();
    }

    var info = (logEntry) => {
        _log(logEntry, styles.info);
    }

    var debug = (logEntry) => {  
        if(logEntry.data){
            console.groupCollapsed(`%c${logEntry.timestamp}${logEntry.level}${logEntry.message}`, styles.debug);
            console.log(logEntry.data);
            console.groupEnd();
        }
        else{
            _log(logEntry, styles.debug);
        }
    }

    return {
        error: error,
        warning: warning,
        info: info,
        debug: debug
    }
}

export default useConsoleLog;
