import React, {useState} from 'react';
import ModalHeader from './ModalHeader'

const useListFormBuilder = () => {
    const [state, setState] = useState();
    var state1 = {};
    var setState1 = (val) => {
        console.log(val);
    }

    var build = ({title, list, onSubmit, onCancel}) => {
        return {
            header: <ModalHeader title={title} onCancel={onCancel}/>,
            body: 
                    <ListDetailsFormBody list={list} onChange={setState1}></ListDetailsFormBody>,
            footer: 
                    <ListDetailsFooter onSubmit={onSubmit} value={state1}></ListDetailsFooter>
        }
    }

    return build;
}

const ListDetailsFooter = ({onSubmit, value}) => {
    return(
        <div className="location-edit-form-buttons">
            {<button 
                    type="button" 
                    className="btn btn-primary" 
                    onClick={() => 
                        onSubmit(value)}
                    disabled={!value.name}>
                Save</button>}
        </div>
    )
}


const ListDetailsFormBody = ({list, onChange}) => {
    const [state, setState] = useState(list);

    var handleInputChanged = (e) => {
        setState({...state, [e.target.name]: e.target.value})
        onChange(state);
    }

    var handleCheckboxChanged = (e) => {
        setState({...state, [e.target.name]: e.target.checked})
        onChange(state);
    }

    return(
        <div className="list-form-container">
            <div className="list-form-item">
                <label htmlFor="list-name" className="col-form-label">Name (required)</label>
                <input 
                    name="name" 
                    className="form-control" 
                    id="list-name" 
                    onChange={handleInputChanged}
                    value={state.name || ''}
                    autoFocus/>
            </div>
            <div className="list-form-item">
                <label htmlFor="list-description">Description</label>
                <textarea 
                    name="description" 
                    className="form-control" 
                    onChange={handleInputChanged}
                    rows="5" id="list-description" 
                    value={state.description || ''}></textarea>
            </div>
            <div className="list-form-item">
                
            <label htmlFor="list-private">Is Private? </label>
                <input 
                    type="checkbox" 
                    id="list-private" 
                    value="false"
                    name="private"
                    onClick={handleCheckboxChanged}/> 
            </div>
        </div>
    )
}

export default useListFormBuilder;
