import React, { createContext, useContext, useReducer } from 'react'


const LocationsListsActions = {
    selectList: 'selectList',
    loadLists: 'loadLists'
}

const LocationsListsState = {
    lists: [],
    selectedList: 1
}

const LocationsListStateContext = createContext();

const LocationsListsStateProvider = ({ children }) => {
    return ( <
        LocationsListStateContext.Provider value = { useReducer(_reducer, LocationsListsState) } > { children } <
        /LocationsListStateContext.Provider>
    )
}

var useLocationsListsState = () => useContext(LocationsListStateContext);

const _reducer = (state, action) => {
    switch (action) {
        case LocationsListsActions.selectList:
            var newState = {...state, selectedList: action.data }
            break;
        default:
            var newState = state;
            break;
    }

    return newState;
}

export default LocationsListsStateProvider;
export { LocationsListStateContext, LocationsListsStateProvider, LocationsListsActions, useLocationsListsState }
