import React, {useEffect} from 'react';
import { useLocationFormState, LocationFormStateActions, LocationFormStateProvider } from './LocationDetailsFormState'
import ModalHeader from '../ModalHeader'

const useListFormBuilder = () => {
    
    var build = ({title, location, onSubmit, onCancel}) => {
        return {
            header: <ModalHeader title={title} onCancel={onCancel}/>,
            body: 
                    <ListDetailsFormBody location={location}></ListDetailsFormBody>,
            footer: 
                    <ListDetailsFooter onSubmit={onSubmit}></ListDetailsFooter>
        }
    }

    return build;
}

const ListDetailsFooter = ({onSubmit}) => {
    const [formState] = useLocationFormState();
    const coordinator = StepsCoordinator();

    var renderSubmit = () => {
        if(formState.step === LastStep){
            return (
                <button 
                    type="button" 
                    className="btn btn-primary" 
                    onClick={() => 
                        onSubmit(formState.location)}
                    disabled={!coordinator.canNext()}>
                Save</button>
            )
        }
    }

    return(
        <div className="location-edit-form-buttons">
            {renderSubmit()}
        </div>
    )
}


const ListDetailsFormBody = (props) => {
    return(
        <div className="list-form-container">
            <div className="list-form-item">
                <label htmlFor="list-name" className="col-form-label">Name (required)</label>
                <input 
                    name="name" 
                    className="form-control" 
                    id="list-name" 
                    value={formState.location.name || ''}
                    autoFocus/>
            </div>
            <div className="list-form-item"></div>
            <div className="list-form-item"></div>
        </div>
    )
}

export default useListFormBuilder;
