const styles = {
    error: 'background: red; color white;font-size: 12pt',
    warning: "background: yellow; color: black",
    info: 'background: white; color red',
    debug: 'background: gray; color: black'
}

const useConsoleLog = () => {
    var _log = (message, style) => {
        console.log(`%c${message}`, style);
    }

    var error = (message) => {
        console.error(message);
    }

    var warning = (message) => {
        console.warn(message);
    }

    var info = (message) => {
        _log(message, styles.info);
    }

    var debug = (message) => {  
        _log(message, styles.debug);
    }

    return {
        error: error,
        warning: warning,
        info: info,
        debug: debug
    }
}

export default useConsoleLog;
